import { Link } from "react-router-dom";
import { useEffect, useState } from 'react';
import React from "react";
import api from "../../api/api";
import Alert from "../alerts/alert";
import Dialogs from "../alerts/dialog";
import LinearProgressLoad from "../alerts/LinearProgress";

import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';

import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';

import InputBase from '@mui/material/InputBase';
import IconButton from '@mui/material/IconButton';
import QrCodeScannerTwoToneIcon from '@mui/icons-material/QrCodeScannerTwoTone';
import SearchIcon from '@mui/icons-material/Search';


function Pos() {
    //user state
    const [products, setProducts] = useState([])
    const [del_id, setId] = useState(-1)

    //alert state
    const [open, setOpen] = useState(false);
    //dialog state
    const [dialogOpen, setDialogOpen] = useState(false);
    const [loadingProgress, setLoadingProgress] = useState(false);
    const [value, setValue] = React.useState('one');

    const handleChangeTab = (event, newValue) => {
        setValue(newValue);
    };


    //dialog open
    const handleClickOpen = (id) => {
        setDialogOpen(true);
        setDialogTitle('Delete')
        setDialogMessage('Are you sure to delete product?')
        setId(id)
    };

    const Item = styled(Paper)(({ theme }) => ({
        ...theme.typography.body2,
        padding: theme.spacing(1),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    }));

    const [alertType, setAlertType] = useState("");
    const [alertMessage, setAlertMessage] = useState("");

    const [dialogTitle, setDialogTitle] = useState("");
    const [dialogMessage, setDialogMessage] = useState("");

    const [age, setAge] = React.useState('');

    const handleChange = (event) => {
        setAge(event.target.value);
    };

    const handleAlertClose = () => {
        setOpen(false);
        setAlertMessage('')
        setAlertType('')
    };

    const handleDialogClose = () => {
        setDialogOpen(false);
        setDialogTitle('')
        setDialogMessage('')
    };


    // retrieve products
    const retrieveProducts = () => {
        setLoadingProgress(true)

        api.get('/fetchAllProducts'
            , {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            }
        )
            .then(function (response) {
                // console.log(response.data)
                setLoadingProgress(false)

                if (response.data.error === false) {

                    // console.log(response.data.users)
                    setProducts(response.data.products)

                } else {
                    // console.log(response.data.message)
                    // return [];
                    setAlertType('error')
                    setAlertMessage(response.data.message)
                    setOpen(true)
                }

            })
            .catch(function (error) {
                setLoadingProgress(false)

                setAlertType('error')
                setAlertMessage("Error 500: Internal server error")
                setOpen(true)
            });
    }

    //delete category
    const dialogAction = () => {
        setOpen(false)
        setId(-1)
        if (del_id > 0) {
            setLoadingProgress(true)

            console.log("ID: " + del_id);

        } else {

        }

    }

    useEffect(() => {
        console.log(api)
        retrieveProducts();
    }, [])


    const loading = () => {
        if (loadingProgress) {
            return (
                <div className="mb-3">
                    <LinearProgressLoad />
                </div>
            )
        }
    }
    return (
        <div className="">
            <div className="header bg-primary pb-6">
                <div className="container-fluid">
                    <div className="header-body">
                        <div className="row align-items-center py-4">
                            <div className="col-lg-6 col-7">
                                <nav aria-label="breadcrumb" className="d-none d-md-inline-block ml-md-4">
                                    <ol className="breadcrumb breadcrumb-links breadcrumb-dark">
                                        <li className="breadcrumb-item"><Link to={"/"}><i className="fas fa-home"></i></Link></li>
                                        <li className="breadcrumb-item active" aria-current="page">POS</li>
                                    </ol>
                                </nav>
                            </div>
                            <div className="col-lg-6 col-5 text-right">

                            </div>


                        </div>
                    </div>
                </div>
            </div>

            <div className="container-fluid mt--6">
                <div className="row">
                    <div className="col">
                        <div className="card">
                            {loading()}

                            <div className="container-fluid mt-5">

                            </div>

                            <Box sx={{ flexGrow: 2 }}>
                                <Grid container spacing={2} columns={16}>
                                    <Grid item xs={8}>
                                        <Item>
                                            <Box sx={{ width: '100%' }}>
                                                <Tabs
                                                    value={value}
                                                    onChange={handleChangeTab}
                                                    textColor="primary"
                                                    indicatorColor="primary"
                                                    aria-label="primary tabs example"
                                                >
                                                    <Tab value="one" label="All" />
                                                    <Tab value="two" label="Item One" />
                                                    <Tab value="three" label="Item Two" />
                                                    <Tab value="5" label="Item Three" />
                                                </Tabs>
                                            </Box>

                                            <Paper
                                                component="form"
                                                sx={{ p: '2px 4px', display: 'flex', alignItems: 'center', width: 400 }}
                                            >
                                                <IconButton sx={{ p: '10px' }} aria-label="menu">
                                                    <SearchIcon />
                                                </IconButton>
                                                <InputBase
                                                    sx={{ ml: 1, flex: 1 }}
                                                    placeholder="Search Google Maps"
                                                    inputProps={{ 'aria-label': 'search google maps' }}
                                                />

                                                <IconButton color="primary" sx={{ p: '10px' }} aria-label="directions">
                                                    <QrCodeScannerTwoToneIcon />
                                                </IconButton>
                                            </Paper>

                                        </Item>
                                    </Grid>
                                    <Grid item xs={8}>
                                        <Item>xs=8</Item>
                                    </Grid>
                                </Grid>
                            </Box>

                        </div>
                    </div>
                </div>
            </div>

            <Dialogs dialogOpen={dialogOpen} dialogAction={dialogAction} dialogTitle={dialogTitle} dialogMessage={dialogMessage} handleDialogClose={handleDialogClose} />
            <Alert openAlert={open} alertMessage={alertMessage} alertType={alertType} handleAlertClose={handleAlertClose} />
        </div>
    );
}

export default Pos;
