import {Link} from "react-router-dom";
import React, {useState} from 'react'
import {enUS, es} from 'date-fns/locale'
import {DatePicker} from 'react-nice-dates'
import 'react-nice-dates/build/style.css'
import { useDateInput } from 'react-nice-dates'


export function DatePickerStartEnd(props) {
    const [date, setDate] = useState()
    const date2 = new Date();
    const inputProps1 = useDateInput({
        date,
        format: 'yyyy-MM-dd',
        locale: 'enUs',
        onDateChange: setDate,
        readOnly: true
    })

    return (
        <div>

            <DatePicker  maximumDate={date2} date={date} onDateChange={setDate} locale={enUS}>
                {({inputProps, focused}) => (


                    <input max='2022-02-9' placeholder='Start date' type="date" placeholder={'From'}
                           className={'form-control input' + (focused ? ' -focused' : '')} {...inputProps1} required/>
                )}
            </DatePicker>
        </div>
    )
}
