import { Link } from "react-router-dom";
import { useEffect, useState } from 'react';
import React from "react";
import api from "../../api/api";
import Alert from "../alerts/alert";
import Dialogs from "../alerts/dialog";
import LinearProgressLoad from "../alerts/LinearProgress";
import ProductCard from "./ProductCard";

import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';

import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
// import Grid from "@material-ui/core/Grid";

import Typography from '@mui/material/Typography';

import Badge from '@mui/material/Badge';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';


import TableCart from "./TableCart";
import Button from '@mui/material/Button';
import ProductsLeft from "./ProductsLeft";
import Autocomplete from '@mui/material/Autocomplete';
import TextField from '@mui/material/TextField';

import InputBase from '@mui/material/InputBase';
import IconButton from '@mui/material/IconButton';
import QrCodeScannerTwoToneIcon from '@mui/icons-material/QrCodeScannerTwoTone';
import SearchIcon from '@mui/icons-material/Search';
import Divider from '@mui/material/Divider';
import AddIcon from '@mui/icons-material/Add';
import AddCustomerModal from "../customers/AddCustomerModal";


function ProductsRight() {
    return (
        <Item>
            <Typography spacing={2} style={{ textAlign: 'left' }} variant="h5" gutterBottom component="div">
                Shopping Cart
                <IconButton spacing={2} aria-label="cart">
                    <StyledBadge badgeContent={cartItems.length} color="secondary">
                        <ShoppingCartIcon />
                    </StyledBadge>
                </IconButton>
            </Typography>
            <Grid container spacing={2} columns={16} sx={{ mb: '20px', mt: '10px' }}>

                <Paper
                    container
                    style={{ width: "100%" }}
                    component="form"
                    sx={{ p: '2px 4px', ml: '20px', mr: '20px', mb: '10px', display: 'flex', alignItems: 'center', width: '100%' }}
                >

                    <Autocomplete
                        id="free-solo-demo"
                        freeSolo
                        options={customers}
                        style={{ width: '80%', padding: '2px ' }}
                        getOptionLabel={customers => (customers.customer_name)}
                        onChange={handleAutocomplete}
                        renderInput={params => (<TextField
                            {...params}
                            label="Select customer..."
                            fullWidth />
                        )}
                    />


                    <Divider sx={{ height: 28, m: 0.5 }} orientation="vertical" />

                    <IconButton onClick={handleClickOpen} color="primary" sx={{ p: '10px' }} aria-label="directions">
                        <AddIcon />
                    </IconButton>
                </Paper>

            </Grid>
            <TableCart style={{ maxHeight: 500, overflow: 'auto' }} cartItems={cartItems} handleClickAddCart={handleClickAddCart} getDeleteProductIdFromCart={handleDeleteFromCart} />



            <Button style={{ width: "100%" }} sx={{ mt: 4 }} variant="outlined">Chekout</Button>


        </Item>

    );
}

export default ProductsRight;
