import {Link, useNavigate, useLocation} from "react-router-dom";
import {useEffect, useState} from 'react';
import api from "../../api/api";
import Alert from "../alerts/alert";
import LinearProgressLoad from "../alerts/LinearProgress";


function CategoryDetails(props) {
    const location = useLocation()
    const category = location.state.category;
    // console.log(user)

    const navigate = useNavigate();


    //alert state
    const [open, setOpen] = useState(false);
    //dialog state

    const [alertType, setAlertType] = useState("");
    const [alertMessage, setAlertMessage] = useState("");
    const [loadingProgress, setLoadingProgress] = useState(false);


    const handleAlertClose = () => {
        setOpen(false);
        setAlertMessage('')
        setAlertType('')
    };


    const [category_name, setCategoryName] = useState(category.category_name);
    const [category_notes, setCategoryNotes] = useState(category.category_notes);


    const updateCategory = (e) => {
        // console.log("sjsjs")
        e.preventDefault()
        if (category_name === "") {
            setOpen(true);
            setAlertMessage('Category name field is mandatory')
            setAlertType('error')
        } else {
            setLoadingProgress(true)

            const state = {
                ecategory_name: category_name,
                ecategory_description: category_notes,
                ecategory_id: category.id
            }
            AddCategoryHandler(state)
            setCategoryName(category_name) // clear form fields 
            setCategoryNotes(category_notes) // clear form fields 

        }
    }

    const AddCategoryHandler = (state) => {
        console.log(state)

        api.put('/updateCategory'
            , state
        )
            .then(function (response) {
                setLoadingProgress(false)

                // console.log(response.data)
                if (response.data.error == false) {

                    // console.log(response.data.users)
                    // return response.data.users;
                    setAlertType('success')
                    setAlertMessage(response.data.message)
                    setOpen(true)
                    setTimeout(() => {
                        navigate('/categories');
                    }, 1000)

                } else {
                    // console.log(response.data.message)
                    // return [];
                    setAlertType('error')
                    setAlertMessage(response.data.message)
                    setOpen(true)
                }

            })
            .catch(function (error) {
                setLoadingProgress(false)
                console.log(error);


                if (error.response.status === 401) {
                    navigate('/login')
                    //place your reentry code
                    console.log('Unauthorised')
                    sessionStorage.removeItem('status')
                    sessionStorage.removeItem('jwt_token')
                    setAlertType('error')
                    setAlertMessage("Error 401: Unauthorised user")
                    setOpen(true)
                } else {
                    console.log('unknown error')
                    window.sessionStorage.setItem('status', false)
                    setAlertType('error')
                    setAlertMessage("Error 500: Internal server error")
                    setOpen(true)
                }
            });
    }

    const loading = () => {
        if (loadingProgress) {
            return (
                <div className="mb-3">
                    <LinearProgressLoad/>
                </div>
            )
        }
    }

    return (
        <div>
            <div className="">
                <div className="header bg-primary pb-6">
                    <div className="container-fluid">
                        <div className="header-body">
                            <div className="row align-items-center py-4">
                                <div className="col-lg-6 col-7">
                                    <nav aria-label="breadcrumb" className="d-none d-md-inline-block ml-md-4">
                                        <ol className="breadcrumb breadcrumb-links breadcrumb-dark">
                                            <li className="breadcrumb-item"><Link to={"/"}><i
                                                className="fas fa-home"></i></Link></li>
                                            <li className="breadcrumb-item active" aria-current="page"><Link
                                                to={'/categories'}>Categories</Link></li>
                                            <li className="breadcrumb-item active" aria-current="page">Edit Category
                                                Details
                                            </li>
                                        </ol>
                                    </nav>
                                </div>

                            </div>
                        </div>
                    </div>
                </div>

            </div>
            <div className="container-fluid mt--6">
                <div className="row">
                    <div className="col">
                        <div className="card">
                            <div className="container-fluid mt-5">
                                {loading()}
                                <form onSubmit={updateCategory}>

                                    <input type="hidden" value={category.id} required/>

                                    <div className="row">


                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Category Name <span className='text-danger'>*</span></label>
                                                <input value={category_name}
                                                       onChange={(e) => setCategoryName(e.target.value)} type="text"
                                                       className="form-control" placeholder="Ex: Electronics" required/>
                                            </div>
                                        </div>

                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label>Category Description</label>
                                                <textarea value={category_notes}
                                                          onChange={(e) => setCategoryNotes(e.target.value)} type="text"
                                                          className="form-control"
                                                          placeholder="Ex: contains gadgets that use electricity"></textarea>
                                            </div>
                                        </div>

                                    </div>

                                    <div className="modal-footer">
                                        <button type="submit" id="btn_add" className="btn btn-primary">Save</button>
                                    </div>

                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <Alert openAlert={open} alertMessage={alertMessage} alertType={alertType}
                   handleAlertClose={handleAlertClose}/>
        </div>
    );
}

export default CategoryDetails;
