import { Link } from "react-router-dom";
import { useEffect, useState } from 'react';
import api from "../../api/api";



function AddUser() {

    const [branches, setBranches] = useState([])
    // retrieve users
    const retrieveBranches = () => {
        api.get('/fetchAllBranches'
            , {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            }
        )
            .then(function (response) {
                console.log(response.data)
                if (response.data.error == false) {

                    // console.log(response.data.users)
                    setBranches(response.data.branches)
                    // return response.data.users;

                } else {
                    console.log("sjs")
                    // return [];

                }

            })
            .then(function (myJson) {
                // console.log(myJson);
            });
    }

    useEffect(() => {
        retrieveBranches();
    }, [])

    const renderBranchList = branches.map((branch) => {
        return (
            <option value={branch.id} >{branch.branch_name}</option>
        );
    })

    return (
        <div>
            <div className="container-fluid">
                <div className="header bg-primary pb-6">
                    <div className="container-fluid">
                        <div className="header-body">
                            <div className="row align-items-center py-4">
                                <div className="col-lg-6 col-7">
                                    <nav aria-label="breadcrumb" className="d-none d-md-inline-block ml-md-4">
                                        <ol className="breadcrumb breadcrumb-links breadcrumb-dark">
                                            <li className="breadcrumb-item"><a href="/"><i className="fas fa-home"></i></a></li>
                                            <li className="breadcrumb-item active" aria-current="page"><Link to={'/users'}>Users</Link></li>
                                            <li className="breadcrumb-item active" aria-current="page">Add User</li>
                                        </ol>
                                    </nav>
                                </div>

                            </div>
                        </div>
                    </div>
                </div>

                <div className="container-fluid mt--6">
                    <div className="row">
                        <div className="col">
                            <div className="card">
                                <div className="container-fluid mt-5">
                                    <form method="POST" id="addUserForm">
                                        <div className="row">
                                            <div className=" col-md-6">
                                                <div className="form-group">
                                                    <label >User role</label>
                                                    <select className="form-control" name="urole" id="urole" required>
                                                        <option disabled selected>-select-</option>
                                                        <option value="admin">Admin</option>
                                                        <option value="manager">Manager</option>
                                                        <option value="shop_user">Assistant</option>
                                                    </select>
                                                </div>
                                            </div>

                                            <div className=" col-md-6">
                                                <div className="form-group">
                                                    <label >Branch</label>
                                                    <select className="form-control" name="ubranch" id="ubranch" required>
                                                        <option selected disabled>-select-</option>
                                                        {renderBranchList}
                                                    </select>
                                                </div>
                                            </div>

                                            <div className="col-md-6">
                                                <div className="form-group">
                                                    <label >Firstname</label>
                                                    <input type="text" className="form-control" name="fname" id="fname" placeholder="Ex: John" required />
                                                </div>
                                            </div>

                                            <div className="col-md-6">
                                                <div className="form-group">
                                                    <label >Lastname</label>
                                                    <input type="text" className="form-control" name="lname" id="lname" placeholder="Ex: Doe" required />
                                                </div>
                                            </div>

                                            <div className="col-md-6">
                                                <div className="form-group">
                                                    <label >Email address</label>
                                                    <input type="email" className="form-control" name="email" id="email" placeholder="Ex: email@example.com" required />
                                                </div>
                                            </div>

                                            <div className="col-md-6">
                                                <div className="form-group">
                                                    <label >Password</label>
                                                    <input type="password" className="form-control" name="password" id="password" placeholder="Password" required />
                                                </div>
                                            </div>

                                        </div>


                                        <div className="modal-footer">
                                            <button type="submit" id="btn_add" className="btn btn-primary">Save</button>
                                        </div>

                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>


            </div>
        </div>
    );
}

export default AddUser;
