import { Link } from "react-router-dom";
import { useEffect, useState } from 'react';
import React from "react";
import UserListRow from "./UserListRow";
import api from "../../api/api";
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';
import { Stack } from "@mui/material";

const Alert = React.forwardRef(function Alert(props, ref) {
    return <MuiAlert elevation={6} ref={ref} variant="filled" {...props} />;
});
function Users() {
    //user state
    const [users, setUsers] = useState([])
    const [del_id, setId] = useState(-1)
    const [alertType, setAlertType] = useState("");
    const [alertMessage, setAlertMessage] = useState("");

    //dialog state
    const [open, setOpen] = useState(false);
    const [openAlert, setAlertOpen] = React.useState(false);

    const handleClickOpen = (id) => {
        setOpen(true);
        setId(id)
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleAlertOpen = () => {
        setAlertOpen(true);
    };

    const handleAlertClose = () => {
        setAlertOpen(false);
        setAlertMessage('')
        setAlertType('')
    };


    // retrieve users
    const retrieveUsers = () => {
        api.get('/fetchAllUsers'
            , {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            }
        )
            .then(function (response) {
                // console.log(response.data)
                if (response.data.error == false) {

                    // console.log(response.data.users)
                    setUsers(response.data.users)
                    // return response.data.users;

                } else {
                    console.log(response.data.message)
                    // return [];

                }

            })
            .catch(function (error) {
                console.log(error);
            });
    }

    //delete contact
    const removeUserHandler = () => {
        setOpen(false)
        setId(-1)
        if (del_id > 0) {
            // console.log("ID: " + id);
            api.delete('/deleteUser/' + del_id + ''
                , {
                    headers: {
                        'Content-Type': 'application/json',
                        'Accept': 'application/json'
                    }
                }
            )
                .then(function (response) {
                    // console.log(response.data)
                    if (response.data.error == false) {

                        const usersCopy = users.filter((user) => {
                            return user.id != del_id
                        })

                        setUsers(usersCopy)
                        setAlertType('success')
                        setAlertMessage('User deleted successfully')
                        handleAlertOpen()


                    } else {
                        console.log(response.data.message)
                        // return [];
                        setAlertType('error')
                        setAlertMessage(response.data.message)
                        handleAlertOpen()
                    }

                })
                .catch(function (myJson) {
                    console.log(myJson);
                    setAlertType('error')
                    setAlertMessage("Error 500: Internal server error")
                    handleAlertOpen()
                });
        } else {

        }

    }

    useEffect(() => {
        retrieveUsers();
    }, [])

    const renderUserList = users.map((user) => {
        return (
            <UserListRow key={user.id} user={user} getDeleteUserId={handleClickOpen} ></UserListRow>
        );
    })

    return (
        <div className="container-fluid">
            <div className="header bg-primary pb-6">
                <div className="container-fluid">
                    <div className="header-body">
                        <div className="row align-items-center py-4">
                            <div className="col-lg-6 col-7">
                                <nav aria-label="breadcrumb" className="d-none d-md-inline-block ml-md-4">
                                    <ol className="breadcrumb breadcrumb-links breadcrumb-dark">
                                        <li className="breadcrumb-item"><a href="/"><i className="fas fa-home"></i></a></li>
                                        <li className="breadcrumb-item active" aria-current="page">Users</li>
                                    </ol>
                                </nav>
                            </div>
                            <div className="col-lg-6 col-5 text-right">
                                <a href="/customers" type="button" className="btn btn-sm btn-neutral">Customers</a>

                                <Link to={'addUser'} >
                                    <button type="button" className="btn btn-sm btn-neutral my-1">New User</button>
                                </Link>

                            </div>


                        </div>
                    </div>
                </div>
            </div>

            <div className="container-fluid mt--6">
                <div className="row">
                    <div className="col">
                        <div className="card">

                            <div className="table-responsive mt-4" id="">
                                <table className="table table-hover mt-3 " id="users_tbl" >
                                    <thead className="thead">
                                        <tr>
                                            <th >Fullname</th>
                                            <th >Email</th>
                                            <th >Role</th>
                                            <th >Status</th>
                                            <th >Action</th>
                                        </tr>
                                    </thead>
                                    <tbody className="">
                                        {renderUserList}
                                    </tbody>
                                </table>
                            </div>

                        </div>
                    </div>
                </div>
            </div>

            <Dialog
                open={open}
                onClose={handleClose}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
                <DialogTitle id="alert-dialog-title">
                    {"Delete user"}
                </DialogTitle>
                <DialogContent>
                    <DialogContentText id="alert-dialog-description">
                        Are you sure you want to delete user?
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose}>Close</Button>
                    <Button className="text-danger" onClick={removeUserHandler} >
                        Yes
                    </Button>
                </DialogActions>
            </Dialog>

            <Stack spacing={2} sx={{ width: '100%' }}>

                <Snackbar open={openAlert} autoHideDuration={6000} onClose={handleAlertClose}>
                    <Alert onClose={handleAlertClose} severity={alertType} sx={{ width: '100%' }}>
                        {alertMessage}
                    </Alert>
                </Snackbar>
            </Stack>
        </div>
    );
}

export default Users;
