import {Link} from "react-router-dom";
import {useEffect, useState} from 'react';
import React from "react";
import ProductListRow from "./ProductListRow";
import api from "../../api/api";
import Alert from "../alerts/alert";
import Dialogs from "../alerts/dialog";
import LinearProgressLoad from "../alerts/LinearProgress";
import BaseUrl from '../../api/baseUrl';


import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TablePagination from '@mui/material/TablePagination';
import TableRow from '@mui/material/TableRow';


function Products() {
    //user state
    const [products, setProducts] = useState([])
    const [del_id, setId] = useState(-1)

    //alert state
    const [open, setOpen] = useState(false);
    //dialog state
    const [dialogOpen, setDialogOpen] = useState(false);
    const [loadingProgress, setLoadingProgress] = useState(false);


    //dialog open
    const handleClickOpen = (id) => {
        setDialogOpen(true);
        setDialogTitle('Delete')
        setDialogMessage('Are you sure to delete product?')
        setId(id)
    };


    const [alertType, setAlertType] = useState("");
    const [alertMessage, setAlertMessage] = useState("");

    const [dialogTitle, setDialogTitle] = useState("");
    const [dialogMessage, setDialogMessage] = useState("");


    const handleAlertClose = () => {
        setOpen(false);
        setAlertMessage('')
        setAlertType('')
    };

    const handleDialogClose = () => {
        setDialogOpen(false);
        setDialogTitle('')
        setDialogMessage('')
    };


    // retrieve products
    const retrieveProducts = () => {
        setLoadingProgress(true)

        api.get('/fetchAllProducts'
        )
            .then(function (response) {
                // console.log(response.data)
                setLoadingProgress(false)

                if (response.data.error === false) {

                    // console.log(response.data.users)
                    setProducts(response.data.products)

                } else {
                    // console.log(response.data.message)
                    // return [];
                    setAlertType('error')
                    setAlertMessage(response.data.message)
                    setOpen(true)
                }

            })
            .catch(function (error) {
                setLoadingProgress(false)

                setAlertType('error')
                setAlertMessage("Error 500: Internal server error")
                setOpen(true)
            });
    }

    //delete product
    const dialogAction = () => {
        setOpen(false)
        setId(-1)
        if (del_id > 0) {
            setLoadingProgress(true)

            // console.log("ID: " + id);
            api.delete('/deleteProduct/' + del_id + ''
                , {
                    headers: {
                        'Content-Type': 'application/json',
                        'Accept': 'application/json'
                    }
                }
            )
                .then(function (response) {
                    // console.log(response.data)
                    setLoadingProgress(false)

                    if (response.data.error === false) {

                        const productsCopy = products.filter((product) => {
                            return product.id !== del_id
                        })
                        handleDialogClose();
                        setProducts(productsCopy)
                        setAlertType('success')
                        setAlertMessage('Product deleted successfully')
                        setOpen(true)


                    } else {
                        // console.log(response.data.message)
                        // return [];
                        setAlertType('error')
                        setAlertMessage(response.data.message)
                        setOpen(true)
                    }

                })
                .catch(function (myJson) {
                    setLoadingProgress(false)

                    console.log(myJson);
                    setAlertType('error')
                    setAlertMessage("Error 500: Internal server error")
                    setOpen(true)
                });
        } else {

        }

    }

    useEffect(() => {
        retrieveProducts();
    }, [])

    const renderProductList = products.map((product) => {
        return (
            <ProductListRow key={`d${product.id}`} product={product}
                            getDeleteProductId={handleClickOpen}></ProductListRow>
        );
    })
    const loading = () => {
        if (loadingProgress) {
            return (
                <div className="mb-3">
                    <LinearProgressLoad/>
                </div>
            )
        }
    }

    //table stuff
    const columns = [
        {id: 'product', label: 'Product Name', minWidth: 170},
        {id: 'branch', label: 'Branch', minWidth: 170},
        {id: 'category', label: 'Category', minWidth: 200},
        {
            id: 'unit',
            label: 'Unit',
            minWidth: 10,
        },
        {
            id: 'price',
            label: 'Price',
            minWidth: 50,
        },
        {
            id: 'quantity',
            label: 'Qty',
            minWidth: 50,
        },
        {
            id: 'action',
            label: 'Action',
            minWidth: 60,
        },
    ];

    function createData(branch, category, unit, price, quantity) {
        return {branch, category, unit, price, quantity};
    }

    const rows = [
        createData('India', 'IN', 1324171354, 3287263, 1)
    ]

    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };
    return (
        <div className="">
            <div className="header bg-primary pb-6">
                <div className="container-fluid">
                    <div className="header-body">
                        <div className="row align-items-center py-4">
                            <div className="col-lg-6 col-7">
                                <nav aria-label="breadcrumb" className="d-none d-md-inline-block ml-md-4">
                                    <ol className="breadcrumb breadcrumb-links breadcrumb-dark">
                                        <li className="breadcrumb-item"><Link to={"/"}><i
                                            className="fas fa-home"></i></Link></li>
                                        <li className="breadcrumb-item active" aria-current="page">Products</li>
                                    </ol>
                                </nav>
                            </div>
                            <div className="col-lg-6 col-5 text-right">
                                <Link to={'/products/product_brands'} type="button"
                                      className="btn btn-sm btn-neutral">Product
                                    Brands</Link>

                                <Link to={'addProduct'}>
                                    <button type="button" className="btn btn-sm btn-neutral my-1">New Product
                                    </button>
                                </Link>

                            </div>


                        </div>
                    </div>
                </div>
            </div>

            <div className="container-fluid mt--6">
                <div className="row">
                    <div className="col">
                        <div className="card">
                            {loading()}
                            <div className="table-responsive mt-4" id="">
                                {/*<table className="table align-items-center table-flush table-hover mt-1" id="units_tbl">*/}
                                {/*    <thead className="thead-light">*/}
                                {/*    <tr>*/}
                                {/*        <th>Name</th>*/}
                                {/*        <th>Branch</th>*/}
                                {/*        <th>Category</th>*/}
                                {/*        <th>Unit</th>*/}
                                {/*        <th>Price</th>*/}
                                {/*        <th>Qty</th>*/}
                                {/*        <th>Action</th>*/}
                                {/*    </tr>*/}
                                {/*    </thead>*/}
                                {/*    <tbody className="">*/}
                                {/*    {renderProductList}*/}
                                {/*    </tbody>*/}
                                {/*</table>*/}
                            </div>
                            <Paper sx={{width: '100%', overflow: 'hidden'}}>
                                <TableContainer sx={{maxHeight: 440}}>
                                    <Table stickyHeader aria-label="sticky table">
                                        <TableHead>
                                            <TableRow>
                                                {columns.map((column) => (
                                                    <TableCell
                                                        key={column.id}
                                                        align={column.align}
                                                        style={{minWidth: column.minWidth}}
                                                    >
                                                        {column.label}
                                                    </TableCell>
                                                ))}
                                            </TableRow>
                                        </TableHead>
                                        <TableBody>
                                            {products
                                                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                                .map((row) => {
                                                    let count = 0;
                                                    return (
                                                        <TableRow hover role="checkbox" tabIndex={-1}
                                                                  key={row.id}>

                                                            <TableCell key={count}>
                                                                <TableHead>
                                                                    <div className="media align-items-center">
                                                                        <a href="#" className="avatar rounded-circle mr-3">
                                                                            <img height={'100%'} width={'100%'} alt="Image placeholder" src={`${BaseUrl}/storage/product_images/${row.img_url}`} />
                                                                        </a>
                                                                        <div className="media-body">
                                                                            <span className="name mb-0 text-sm"> {row.product_name}</span>
                                                                        </div>
                                                                    </div>
                                                                </TableHead>
                                                            </TableCell>

                                                            <TableCell key={count}>
                                                                {row.branch_name}
                                                            </TableCell>
                                                            <TableCell >
                                                                {row.category_name}
                                                            </TableCell>
                                                            <TableCell >
                                                                {row.unit_name} -
                                                                {row.unit_symbol}
                                                            </TableCell>
                                                            <TableCell >
                                                                {row.product_price}
                                                            </TableCell>
                                                            <TableCell >
                                                                {row.product_qty}
                                                            </TableCell>
                                                            <TableCell >
                                                                <div className="dropdown">
                                                                    <a className="btn btn-sm btn-icon-only text-dark" href="#" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                                                        <i className="fas fa-ellipsis-h"></i>
                                                                    </a>
                                                                    <div className="dropdown-menu dropdown-menu-right dropdown-menu-arrow">
                                                                        <Link to={'editProduct/' + row.id}
                                                                              state={{ product: row }}
                                                                              className="text-primary dropdown-item editIcon" >Edit</Link>

                                                                        <button onClick={() => handleClickOpen(row.id)} className="dropdown-item text-danger">Delete</button>

                                                                    </div>
                                                                </div>
                                                            </TableCell>

                                                        </TableRow>
                                                    );

                                                    count++
                                                })}
                                        </TableBody>
                                    </Table>
                                </TableContainer>
                                <TablePagination
                                    rowsPerPageOptions={[10, 25, 100]}
                                    component="div"
                                    count={products.length}
                                    rowsPerPage={rowsPerPage}
                                    page={page}
                                    onPageChange={handleChangePage}
                                    onRowsPerPageChange={handleChangeRowsPerPage}
                                />
                            </Paper>
                        </div>
                    </div>
                </div>
            </div>

            <Dialogs dialogOpen={dialogOpen} dialogAction={dialogAction} dialogTitle={dialogTitle}
                     dialogMessage={dialogMessage} handleDialogClose={handleDialogClose}/>
            <Alert openAlert={open} alertMessage={alertMessage} alertType={alertType}
                   handleAlertClose={handleAlertClose}/>
        </div>
    );
}

export default Products;
