import Button from '@mui/material/Button';
import { useEffect, useState } from 'react';
import Box from '@mui/material/Box';
import IconButton from '@mui/material/IconButton';
import Input from '@mui/material/Input';
import FilledInput from '@mui/material/FilledInput';
import OutlinedInput from '@mui/material/OutlinedInput';
import InputLabel from '@mui/material/InputLabel';
import InputAdornment from '@mui/material/InputAdornment';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import TextField from '@mui/material/TextField';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormLabel from '@mui/material/FormLabel';

function Checkout(props) {
    console.log(props)
    const cartItems = props.cartItems;
    let total = 0;

    const [values, setValues] = useState({
        amount_paid: '',
    });

    const formatNumber = (number) => {
        var nf = new Intl.NumberFormat();
        var result = nf.format(number); // "1,234,567,890"
        return result;
    }

    const handleChange = (prop) => (event) => {
        setValues({ ...values, [prop]: event.target.value });
    };

    const handleClickShowPassword = () => {
        setValues({
            ...values,
            showPassword: !values.showPassword,
        });
    };

    const handleMouseDownPassword = (event) => {
        event.preventDefault();
    };

    return (
        <Box sx={{ width: '95%', mt: 3 }}>

            <div>


                <FormControl fullWidth sx={{ m: 1 }} variant="standard">
                    <InputLabel htmlFor="standard-adornment-amount">Amount</InputLabel>
                    <Input
                        id="standard-adornment-amount"
                        value={values.amount_paid}
                        onChange={handleChange('amount_paid')}
                        startAdornment={<InputAdornment position="start">MWK</InputAdornment>}
                    />
                </FormControl>

                <FormControl style={{ textAlign: 'left' }} fullWidth sx={{ m: 1 }} variant="standard">
                    <FormLabel htmlFor="html_tax">Apply Tax ({props.tax_amount})</FormLabel>
                    <RadioGroup
                        defaultValue="0"
                        id='html_tax'
                        row
                        aria-labelledby="demo-row-radio-buttons-group-label"
                        name="row-radio-buttons-group"
                    >
                        <FormControlLabel value="0" control={<Radio />} label="No" />
                        <FormControlLabel value="1" control={<Radio />} label="Yes" />


                    </RadioGroup>
                </FormControl>
            </div>
        </Box>

    );
}

export default Checkout;
