import './App.css';
import {useEffect, useState} from 'react';
import {BrowserRouter as Router, Switch, Route, Routes, useNavigate} from 'react-router-dom';
import Users from './components/users/Users';
import Dashboard from './components/dashboard/Dashboard';
import api from './api/api';
import AddUser from './components/users/AddUser';
import UserDetails from './components/users/UserDetails';
import Customers from './components/customers/Customers';
import CustomerDetails from './components/customers/CustomerDetails';
import AddCustomer from './components/customers/AddCustomer';
import Units from './components/units/Units';
import AddUnit from './components/units/AddUnit';
import UnitDetails from './components/units/UnitDetails';
import Categories from './components/categories/Categories';
import AddCategory from './components/categories/AddCategory';
import CategoryDetails from './components/categories/CategoryDetails';
import Branches from './components/branches/Branches';
import AddBranch from './components/branches/AddBranch';
import BranchDetails from './components/branches/BranchDetails';
import SideBar from './components/incl/SideBar';
import TopBar from './components/incl/TopBar';
import Products from './components/products/Products';
import AddProduct from './components/products/AddProduct';
import ProductDetails from './components/products/ProductDetails';

import Brands from './components/brands/Brands';
import AddBrand from './components/brands/AddBrand';
import BrandDetails from './components/brands/BrandDetails';
import Pos from './components/pos/Pos';
import Login from "./components/Login/Login";

function App() {
    const navigate = useNavigate();
    const [login_status, setLoginStatus] = useState(false)

    const checkLoginStatus = () => {
        api.get('/api/auth/user'
            , {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json',
                    'Authorization': 'Bearer ' + window.sessionStorage.getItem("jwt_token")

                }
            }
        )
            .then(function (response) {
                console.log(response)
                console.log("check")
                if (response.data.status !== 401) {
                    setLoginStatus(true)
                    console.log('authorised')
                } else {
                    console.log('unauthorised')
                    setLoginStatus(false)
                }

            })
            .catch(function (error) {
                navigate('/login');
                if (error.response.status === 401) {
                    //place your reentry code
                    console.log('Unauthorised')
                    setLoginStatus(false)
                } else {
                    console.log('unknown error')
                    setLoginStatus(false)
                }

            });
    }

    const topBarContent = () => {
        if (login_status) {
            return (
                <TopBar/>
            )
        }
    }
    const sideBarContent = () => {
        if (login_status) {
            return (
                <SideBar/>
            )
        }
    }

    useEffect(() => {
        checkLoginStatus();
    }, [''])
    // console.log(users)
    return (

        <div id="bg-default">

            {topBarContent()}
            <Router>

                {sideBarContent()}
                <div className='main-content ss ' id='panel'>


                    <Routes>

                        <Route path='/'
                               element={<Dashboard login_status={login_status} setLoginStatus={setLoginStatus}/>}/>
                        <Route path='/login'
                               element={<Login login_status={login_status} setLoginStatus={setLoginStatus}/>}/>


                        {/* users */}
                        <Route path='/users'
                               element={<Users login_status={login_status} setLoginStatus={setLoginStatus}/>}/>
                        <Route path='/users/AddUser'
                               element={<AddUser login_status={login_status} setLoginStatus={setLoginStatus}/>}/>
                        <Route path='/users/editUser/:id'
                               element={<UserDetails login_status={login_status} setLoginStatus={setLoginStatus}/>}/>

                        {/* customers */}
                        <Route path='/customers'
                               element={<Customers login_status={login_status} setLoginStatus={setLoginStatus}/>}/>
                        <Route path='/customers/AddCustomer'
                               element={<AddCustomer login_status={login_status} setLoginStatus={setLoginStatus}/>}/>
                        <Route path='/customers/editCustomer/:id' element={<CustomerDetails login_status={login_status}
                                                                                            setLoginStatus={setLoginStatus}/>}/>

                        {/* units */}
                        <Route path='/units'
                               element={<Units login_status={login_status} setLoginStatus={setLoginStatus}/>}/>
                        <Route path='/units/AddUnit'
                               element={<AddUnit login_status={login_status} setLoginStatus={setLoginStatus}/>}/>
                        <Route path='/units/editUnit/:id'
                               element={<UnitDetails login_status={login_status} setLoginStatus={setLoginStatus}/>}/>

                        {/* categories */}
                        <Route path='/categories'
                               element={<Categories login_status={login_status} setLoginStatus={setLoginStatus}/>}/>
                        <Route path='/categories/AddCategory'
                               element={<AddCategory login_status={login_status} setLoginStatus={setLoginStatus}/>}/>
                        <Route path='/categories/editCategory/:id' element={<CategoryDetails login_status={login_status}
                                                                                             setLoginStatus={setLoginStatus}/>}/>

                        {/* branches */}
                        <Route path='/branches'
                               element={<Branches login_status={login_status} setLoginStatus={setLoginStatus}/>}/>
                        <Route path='/branches/AddBranch'
                               element={<AddBranch login_status={login_status} setLoginStatus={setLoginStatus}/>}/>
                        <Route path='/branches/editBranch/:id'
                               element={<BranchDetails login_status={login_status} setLoginStatus={setLoginStatus}/>}/>

                        {/* products */}
                        <Route path='/products'
                               element={<Products login_status={login_status} setLoginStatus={setLoginStatus}/>}/>
                        <Route path='/products/AddProduct'
                               element={<AddProduct login_status={login_status} setLoginStatus={setLoginStatus}/>}/>
                        <Route path='/products/editProduct/:id'
                               element={<ProductDetails login_status={login_status} setLoginStatus={setLoginStatus}/>}/>

                        {/* product brands */}
                        <Route path='/products/product_brands'
                               element={<Brands login_status={login_status} setLoginStatus={setLoginStatus}/>}/>
                        <Route path='/products/product_brands/AddProductBrand'
                               element={<AddBrand login_status={login_status} setLoginStatus={setLoginStatus}/>}/>
                        <Route path='/products/product_brands/editProductBrand/:id'
                               element={<BrandDetails login_status={login_status} setLoginStatus={setLoginStatus}/>}/>

                        {/* Point of Sale */}
                        <Route path='/pos'
                               element={<Pos login_status={login_status} setLoginStatus={setLoginStatus}/>}/>


                    </Routes>
                </div>
            </Router>
        </div>
    );
}

export default App;
