import { Link } from "react-router-dom";
import { useEffect, useState } from 'react';
import UserListRow from "./UserListRow";
import api from "../../api/api";

function Users() {
    const [users, setUsers] = useState([])
    // retrieve users
    const retrieveUsers = () => {
        api.get('/fetchAllUsers'
            , {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            }
        )
            .then(function (response) {
                // console.log(response.data)
                if (response.data.error == false) {

                    // console.log(response.data.users)
                    setUsers(response.data.users)
                    // return response.data.users;

                } else {
                    console.log("sjs")
                    // return [];
                }

            })
            .then(function (myJson) {
                // console.log(myJson);
            });
    }

    useEffect(() => {
        retrieveUsers();
    }, [])

    const renderUserList = users.map((user) => {
        return (
            <UserListRow key={user.id} user={user} ></UserListRow>
        );
    })

    return (
        <div className="container-fluid">
            <div className="header bg-primary pb-6">
                <div className="container-fluid">
                    <div className="header-body">
                        <div className="row align-items-center py-4">
                            <div className="col-lg-6 col-7">
                                <nav aria-label="breadcrumb" className="d-none d-md-inline-block ml-md-4">
                                    <ol className="breadcrumb breadcrumb-links breadcrumb-dark">
                                        <li className="breadcrumb-item"><a href="/"><i className="fas fa-home"></i></a></li>
                                        <li className="breadcrumb-item active" aria-current="page">Users</li>
                                    </ol>
                                </nav>
                            </div>
                            <div className="col-lg-6 col-5 text-right">
                                <a href="/customers" type="button" className="btn btn-sm btn-neutral">Customers</a>

                                <button data-toggle="modal" data-target="#adduserModal" type="button" className="btn btn-sm btn-neutral my-1">New User</button>
                            </div>


                        </div>
                    </div>
                </div>
            </div>

            <div className="container-fluid mt--6">
                <div className="row">
                    <div className="col">
                        <div className="card">

                            <div className="table-responsive mt-4" id="">
                                <table className="table table-hover mt-3 " id="users_tbl" >
                                    <thead className="thead">
                                        <tr>
                                            <th >Fullname</th>
                                            <th >Email</th>
                                            <th >Role</th>
                                            <th >Status</th>
                                            <th >Action</th>
                                        </tr>
                                    </thead>
                                    <tbody className="">
                                        {renderUserList}
                                    </tbody>
                                </table>
                            </div>

                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Users;
